SELECT * FROM ( SELECT a.*, rownum rnum FROM ( SELECT m.mem_num, m.mem_auth, m.mem_rank, m.mem_id, md.mem_name, md.mem_phone, md.mem_email, md.mem_reg_date FROM member m JOIN member_detail md ON m.mem_num = md.mem_num
) a )
WHERE rnum >= #{start} AND rnum <= #{end}

SELECT * FROM ( SELECT a.*, rownum rnum FROM ( SELECT m.mem_num, m.mem_id, m.mem_rank, m.mem_membership, md.mem_name, md.mem_reg_date FROM member m JOIN member_detail md ON m.mem_num = md.mem_num
WHERE m.mem_membership = 2 ) a )
WHERE rnum >= #{start} AND rnum <= #{end}

SELECT * FROM ( SELECT a.*, rownum rnum FROM ( SELECT event_num, event_name, event_start, event_end, event_reg_date FROM event
) a )
WHERE rnum >= #{start} AND rnum <= #{end}

SELECT * FROM ( SELECT a.*, rownum rnum FROM ( SELECT nb_num, nb_title, nb_hit, nb_reg_date FROM notice_board
) a )
WHERE rnum >= #{start} AND rnum <= #{end}

SELECT * FROM ( SELECT a.*, rownum rnum FROM ( SELECT f_num, f_title, f_hit, f_reg_date FROM faq
) a )
WHERE rnum >= #{start} AND rnum <= #{end}

SELECT * FROM ( SELECT a.*, rownum rnum FROM ( SELECT m_code,
REPLACE(REPLACE(m_name, '<', '&lt;'), '>', '&gt;') AS m_name,
m_opendate, m_companys, m_status, m_filename FROM movie
) a )
WHERE rnum >= #{start} AND rnum <= #{end}

SELECT * FROM ( SELECT a.*, rownum rnum FROM ( SELECT c_num, c_branch, c_location, c_phone, c_parkable FROM cinema
) a )
WHERE rnum >= #{start} AND rnum <= #{end}

 		  SELECT te_name
 		  FROM theater
		  WHERE c_num = #{c_num}

SELECT * FROM ( SELECT a.*, rownum rnum FROM ( SELECT cb_num, mem_num, cb_title, cb_type, cb_report, cb_hit, cb_reg_date FROM community_board
) a )
WHERE rnum >= #{start} AND rnum <= #{end}

SELECT * FROM ( SELECT a.*, rownum rnum FROM ( SELECT ab_num, mem_num, ab_title, ab_type, ab_report, ab_hit, ab_reg_date FROM assignment_board
) a )
WHERE rnum >= #{start} AND rnum <= #{end}

SELECT * FROM ( SELECT a.*, rownum rnum FROM ( SELECT 'reply' AS data_type, cc_num, mem_num, cc_content, cc_report FROM community_comment
ORDER BY cc_report DESC ) a )
WHERE rownum >= #{start} AND rownum <= #{end}

SELECT * FROM ( SELECT a.*, rownum rnum FROM ( SELECT 'comment' AS data_type, te_num, mem_num, te_content, te_report FROM community_response
ORDER BY te_report DESC ) a )
WHERE rownum >= #{start} AND rownum <= #{end}

SELECT * FROM ( SELECT a.*, rownum rnum FROM ( SELECT consult_num, mem_num, consult_title, consult_type, consult_reg_date FROM consult
) a )
WHERE rnum >= #{start} AND rnum <= #{end}
