mapper.select:
SELECT * FROM (SELECT a.*, rownum rnum FROM (SELECT cb_num,
REPLACE(REPLACE(cb_title,'<','&lt;'),'>','&gt;') cb_title,
cb_hit, cb_filename, cb_reg_date, mem_num, mem_id, re_cnt, fav_cnt
FROM community_board LEFT OUTER JOIN (SELECT COUNT(*) re_cnt, board_num FROM spboard_reply GROUP BY board_num) USING(board_num) LEFT OUTER JOIN (SELECT COUNT(*) fav_cnt, board_num FROM spboard_fav GROUP BY board_num) USING(board_num) JOIN spmember USING(mem_num)
)a)
WHERE rnum >= #{start} AND rnum <= #{end}

mapper.update:
UPDATE spboard SET
filename = #{filename},
title = #{title}, content = #{content}, ip = #{ip}, modify_date = SYSDATE WHERE board_num = #{board_num}

mapper.select:
SELECT * FROM (SELECT a.*, rownum rnum FROM (SELECT * FROM spboard_reply JOIN spmember USING(mem_num)
LEFT OUTER JOIN (SELECT re_num, mem_num click_num FROM spreply_fav WHERE mem_num=#{mem_num}) USING (re_num) LEFT OUTER JOIN (SELECT re_num, COUNT(*) refav_cnt FROM spreply_fav GROUP BY re_num) USING (re_num) WHERE board_num=#{board_num} ORDER BY re_num DESC)a)
WHERE rnum >= #{start} AND rnum <= #{end}
