mapper.select:
SELECT * FROM (SELECT a.*, rownum rnum FROM (SELECT cb_num,
REPLACE(REPLACE(cb_title,'<','&lt;'),'>','&gt;') cb_title,
cb_hit, cb_filename, cb_reg_date, mem_num, mem_id, mem_nickname, re_cnt, fav_cnt
FROM community_board LEFT OUTER JOIN (SELECT COUNT(*) re_cnt, cb_num FROM community_comment GROUP BY cb_num) USING (cb_num) LEFT OUTER JOIN (SELECT COUNT(*) fav_cnt, cb_num FROM community_fav GROUP BY cb_num) USING(cb_num) JOIN member USING(mem_num) LEFT OUTER JOIN member_detail USING(mem_num)
)a)
WHERE rnum >= #{start} AND rnum <= #{end}

mapper.update:
UPDATE community_board SET
				cb_type = #{cb_type}, cb_title = #{cb_title}, cb_content = #{cb_content}, cb_ip = #{cb_ip}, cb_modify_date = SYSDATE
										WHERE cb_num = #{cb_num}

mapper.select:
SELECT * FROM (SELECT a.*, rownum rnum FROM (SELECT * FROM community_comment JOIN member USING(mem_num)
LEFT OUTER JOIN (SELECT cc_num, mem_num click_num FROM community_comment_fav WHERE mem_num=#{mem_num}) USING (cc_num) LEFT OUTER JOIN (SELECT cc_num, COUNT(*) refav_cnt FROM community_comment_fav GROUP BY cc_num) USING (cc_num) WHERE cb_num=#{cb_num} ORDER BY cc_num ASC)a)
WHERE rnum >= #{start} AND rnum <= #{end}
