mapper.insert:
INSERT INTO movie_actor (
    m_code,
    actor
) VALUES (
    #{m_code},
    #{actor}
)

INSERT INTO movie_director (
    m_code,
    director
) VALUES (
    #{m_code},
    #{director}
)

INSERT INTO movie_genre (
    m_code,
    genre
) VALUES (
    #{m_code},
    #{genre}
)

where.if:
AND m_code IN (
    SELECT m_code
    FROM movie_genre
    WHERE genre IN
    
        #{genre}
    
)

mapper.select:
SELECT * FROM (SELECT a.*, rownum rnum FROM (SELECT m_code,
REPLACE(REPLACE(m_name,'<','&lt;'),'>','&gt;') m_name,
m_filename, m_opendate, m_companys, m_status, m_content FROM movie LEFT OUTER JOIN (SELECT COUNT (*) bookmark_cnt, m_code FROM movie_bookmark GROUP BY m_code) USING(m_code)
)a)
WHERE rnum >= #{start} AND rnum <= #{end}

SELECT * FROM (SELECT a.*, rownum rnum FROM (SELECT * FROM movie_review JOIN member USING(mem_num) LEFT OUTER JOIN (SELECT mr_num, mem_num click_num FROM mr_fav WHERE mem_num=#{mem_num}) USING(mr_num) LEFT OUTER JOIN (SELECT mr_num, COUNT(*) review_cnt FROM mr_fav GROUP BY mr_num) USING(mr_num) WHERE m_code=#{m_code}
ORDER BY mr_regdate DESC
ORDER BY review_cnt ASC, mr_regdate DESC
) a)
WHERE rnum >=#{start} AND rnum <=#{end}

SELECT * FROM movie WHERE m_code IN ( SELECT m_code FROM movie_genre WHERE genre IN
#{genre}
)
